            {M^2(X)} && {M(X)} && {M^2(X)} \\
            && {M(X)} \\
            \\
            & {M^3(X)} && {M^2(X)} \\
            & {M^2(X)} && {M(X)}
            \arrow ["{\mu _{M(X)}}"', from=4-2, to=5-2]
            \arrow ["{\mu _X}", from=5-2, to=5-4]
            \arrow ["{1_{M(X)}}", from=1-3, to=2-3]
            \arrow ["{\mu _X}"', from=1-1, to=2-3]
            \arrow ["{M(\eta _X)}"', from=1-3, to=1-1]
            \arrow ["{\eta _{ M(X)}}", from=1-3, to=1-5]
            \arrow ["{\mu _X}", from=1-5, to=2-3]
            \arrow ["{\mu _X}", from=4-4, to=5-4]
            \arrow ["{M(\mu _X)}", from=4-2, to=4-4]
        \end {tikzcd}

