            X && {X\sqcup  Y} && Y \\
            \\
            && Z
            \arrow ["{[f,g]}"', dashed, from=1-3, to=3-3]
            \arrow ["f"', from=1-1, to=3-3]
            \arrow ["{i_Y}"', from=1-5, to=1-3]
            \arrow ["{i_X}", from=1-1, to=1-3]
            \arrow ["g", from=1-5, to=3-3]
        \end {tikzcd}

