        \usetikzlibrary {arrows}
        \begin {tikzcd}
            & {X\times _ZY} \\
            \\
            & A \\
            X && Y \\
            & Z
            \arrow ["f"', from=3-2, to=4-1]
            \arrow ["g", from=3-2, to=4-3]
            \arrow ["t"', from=4-1, to=5-2]
            \arrow ["u", from=4-3, to=5-2]
            \arrow ["{\exists !}", dashed, from=3-2, to=1-2]
            \arrow ["{\pi _1}"', bend right, from=1-2, to=4-1]
	        \arrow ["{\pi _2}", bend left, from=1-2, to=4-3]
        \end {tikzcd}



    there exists a unique arrow \langle  f,g \rangle _Z: A\to  X\times _Z Y such that
             \pi _1\circ \langle  f,g \rangle _Z = f \text { and } \pi _2\circ \langle  f,g \rangle _Z = g
