mainmatter.pre:
c <- nextChar();
if (c == 'n') {
    c <- nextChar();
    if (c == 'e') {
        c <- nextChar();
        if (c == 'w') {
            return newToken();
        }
    }
}
reportError();

embedded-tex.embedded-tex-body:

\begin {tikzcd}
   {S_0} & {S_1} & {S_2} & {S_3}
    \arrow ["n", from=1-1, to=1-2]
    \arrow ["e", from=1-2, to=1-3]
    \arrow ["w", from=1-3, to=1-4]
\end {tikzcd}

mainmatter.pre:
state <- s0;
char <-nextChar();
while (state != se and char != eof) {
    state <- delta(state, char);
    char <- nextChar();
}
if (state in SA) {
    return Acceptance();
} else {
    reportError();
}
